// helper variables
var R_ = r / 255;
var G_ = g / 255;
var B_ = b / 255;

var C_max = Math.max(R_, G_, B_);
var C_min = Math.min(R_, G_, B_);
var Delta = C_max - C_min;

// Hue
var H;
if (Delta === 0) {
    H = 0;
} else if (C_max === R_) {
    H = 60 * ( ( (G_ - B_) / Delta ) % 6 );
    if (H < 0) {
        H = H + 360;
    }
} else if (C_max === G_) {
    H = 60 * ( ( (B_ - R_) / Delta) + 2 );
} else if (C_max === B_) {
    H = 60 * ( ( (R_ - G_) / Delta) + 4 );
}

// Saturation
var S;
if (C_max === 0) {
    S = 0;
} else{
    S = Delta / C_max;
}

// Value
